import React, { useState } from 'react'; // React
import PropTypes from 'prop-types'; // PropTypes

import Button from 'components/Button'; // Button
import { ReactComponent as ChevronDown } from 'assets/chevron-down.svg'; // Chevron Down Icon
import { ReactComponent as Checkmark } from 'assets/checkmark.svg'; // Checkmark Icon
import styles from 'styles/components/FilterDropdown.module.css'; // Component styles

const OPTIONS = [
  {
    label: 'Jazz',
    value: 'jazz',
    selected: false,
  },
  {
    label: 'Lyrical',
    value: 'lyrical',
    selected: false,
  },
  {
    label: 'Ballet',
    value: 'ballet',
    selected: false,
  },
];

export default function FilterDropdown({ buttonText = 'button', options = OPTIONS, setOptions }) {
  const [open, setOpen] = useState(false);

  const handleButtonClick = () => setOpen(!open);

  return (
    <div className={styles.filterDropdown__wrapper}>
      <button
        className={`${styles.filterDropdown__button} ${open && styles.filterDropdown__buttonOpen}`}
        onClick={handleButtonClick}
      >
        {buttonText}
        <span className={styles.filterDropdown__button_iconWrapper}>
          <ChevronDown />
        </span>
      </button>
      {open && (
        <div className={styles.filterDropdown__options}>
          {options.map((option, i) => (
            <div key={i} className={styles.filterDropdown__option}>
              <div>
                <Checkmark />
              </div>
              <div>{option.label}</div>
            </div>
          ))}
          <Button className={styles.filterDropdown__clearFilterButton} variant="contained">
            Clear Filter
          </Button>
        </div>
      )}
    </div>
  );
}
