import React from 'react'; // React
import PropTypes from 'prop-types'; // PropTypes

import Select, { components } from 'react-select'; // React Select
import { ReactComponent as ChevronDown } from 'assets/chevron-down.svg'; // Chevron Down Icon
import styles from 'styles/components/Dropdown.module.css'; // Component styles

const OPTIONS = [
  { value: 'novice', label: 'Novice' },
  { value: 'Intermediate 1', label: 'Intermediate 1' },
];

const DropdownIndicator = props => (
  <components.DropdownIndicator {...props}></components.DropdownIndicator>
);

const Option = props => <components.Option className={styles.dropdown__option} {...props} />;

export default function Dropdown({
  className,
  isDisabled = false,
  options = OPTIONS,
  placeholder = 'placeholder',
  selected,
  setSelected = () => {},
}) {
  // TODO: replace classname for dropdown wrapper
  return (
    <div className={styles.dropdown__wrapper}>
      <Select
        components={{
          DropdownIndicator,
          IndicatorSeparator: null,
          Option,
        }}
        className={className}
        classNamePrefix="dropdown"
        defaultValue={selected}
        isDisabled={isDisabled}
        onChange={setSelected}
        placeholder={placeholder}
        options={options}
        theme={theme => ({
          ...theme,
          colors: {
            ...theme.colors,
            primary25: '#f5f6f7',
          },
        })}
        value={selected}
      />
    </div>
  );
}
