import React, { useState, useEffect, useRef, useCallback } from 'react'; // React
import PropTypes from 'prop-types'; // PropTypes

import Button from 'components/Button'; // Button
import { ReactComponent as ChevronDown } from 'assets/chevron-down.svg'; // Chevron Down Icon
import { ReactComponent as Checkmark } from 'assets/checkmark.svg'; // Checkmark Icon
import styles from 'styles/components/FilterDropdown.module.css'; // Component styles

export default function FilterDropdown({ buttonText, options = {}, setOptions }) {
  const [open, setOpen] = useState(false);

  const handleButtonClick = () => setOpen(!open);

  const handleClearFilter = () => {
    const newOptions = {};
    Object.keys(options).map(option => {
      newOptions[option] = {
        ...options[option],
        selected: false,
      };
    });
    setOptions(newOptions);
  };

  const toggleOption = useCallback(
    option => () =>
      setOptions(options => ({
        ...options,
        [option]: {
          ...options[option],
          selected: !options[option].selected,
        },
      })),
    []
  );

  const Option = ({ option }) => {
    const {
      [option]: { selected },
    } = options;
    return (
      <div
        className={`${styles.filterDropdown__option} ${selected &&
          styles.filterDropdown__optionSelected}`}
      >
        <div onClick={toggleOption(option)}>{selected && <Checkmark />}</div>
        <div>{options[option].label}</div>
      </div>
    );
  };

  const buttonRef = useRef(null);
  const optionsRef = useRef(null);
  useEffect(() => {
    window.addEventListener('click', event => {
      if (!optionsRef.current.contains(event.target) && !buttonRef.current.contains(event.target)) {
        setOpen(false);
      }
    });
  });

  return (
    <div className={styles.filterDropdown__wrapper}>
      <button
        className={`${styles.filterDropdown__button} ${open && styles.filterDropdown__buttonOpen}`}
        onClick={handleButtonClick}
        ref={buttonRef}
      >
        {buttonText}
        <span className={styles.filterDropdown__button_iconWrapper}>
          <ChevronDown />
        </span>
      </button>
      {open && (
        <div className={styles.filterDropdown__options__container} ref={optionsRef}>
          <div className={styles.filterDropdown__options}>
            {Object.keys(options).map((option, i) => (
              <Option key={i} option={option} />
            ))}
          </div>
          <Button
            className={styles.filterDropdown__clearFilterButton}
            variant="contained"
            onClick={handleClearFilter}
          >
            Clear Filter
          </Button>
        </div>
      )}
    </div>
  );
}
